(1095, '594', 'Ano, mám to už nastavené ako chcem, len stále je v pozadí malý šedý štvorec:\r\n\r\nmám to na ultra-game.crew.sk\r\n\r\n[code]if (!defined(&quot;IN_FUSION&quot;)) { die(&quot;Access Denied&quot;); }\r\nif (file_exists(INFUSIONS.&quot;css_server_panel/locale/&quot;.$settings[&#039;locale&#039;].&quot;.php&quot;)) { include INFUSIONS.&quot;css_server_panel/locale/&quot;.$settings[&#039;locale&#039;].&quot;.php&quot;; } \r\nelse { include INFUSIONS.&quot;css_server_panel/locale/Czech.php&quot;; }\r\n\r\n$map;\r\n$players;\r\n$servername;\r\n$maxplayers;\r\n$bots;\r\n$success;\r\n\r\n$serveroutput = create_table();\r\n\r\nopentable(&quot;Server Status&quot;);\r\nEcho &quot;&lt;table width=&#039;250%&#039; valign=&#039;top&#039; cellpadding=&#039;1&#039; cellspacing=&#039;1&#039; class=&#039;tbl-border&#039; valign=&#039;top&#039;&gt;\r\n	  &lt;tr&gt;\r\n	  &lt;td class = &#039;tbl2&#039;&gt;&lt;/td&gt;\r\n	  &lt;td class = &#039;tbl2&#039; align=&#039;center&#039;&gt;&lt;span style=&#039;color:white&#039;&gt;Názov serverů&lt;/span&gt;&lt;/td&gt;\r\n	  &lt;td class = &#039;tbl2&#039; align=&#039;center&#039;&gt;&lt;span style=&#039;color:white&#039;&gt;IP Adresa&lt;/span&gt;&lt;/td&gt;\r\n	  &lt;td class = &#039;tbl2&#039; align=&#039;center&#039;&gt;&lt;span style=&#039;color:white&#039;&gt;Mapa&lt;/span&gt;&lt;/td&gt;\r\n	  &lt;td class = &#039;tbl2&#039; align=&#039;center&#039;&gt;&lt;span style=&#039;color:white&#039;&gt;Hráčů&lt;/span&gt;&lt;/td&gt;\r\n	  &lt;td class = &#039;tbl2&#039; width=&#039;5%&#039;&gt;&lt;span style=&#039;color:white&#039;&gt;Status&lt;/span&gt;&lt;/td&gt;\r\n	  &lt;td class = &#039;tbl2&#039; width=&#039;5%&#039;&gt;&lt;span style=&#039;color:white&#039;&gt;VIP&lt;/span&gt;&lt;/td&gt;\r\n	  &lt;td class = &#039;tbl2&#039; align=&#039;center&#039;&gt;&lt;span style=&#039;color:white&#039;&gt;Akce&lt;/span&gt;&lt;/td&gt;\r\n	  &lt;/tr&gt;\r\n     &lt;center&gt;&lt;a href=&#039;/pravidla1.php&#039; target=&#039;_blank&#039;&gt