ion */\r\n\r\n.pagenav {\r\n	padding: 0.4em;\r\n}\r\n\r\n.pagenav span {\r\n	color: #fff;\r\n	background-color: #0a0a0a;\r\n	border: 1px solid #111;\r\n	padding: 0.2em 0.4em 0.2em 0.4em;\r\n	margin: 0.2em;\r\n}\r\n\r\n.pagenav a {\r\n	color: #fff;\r\n	background-color: #0a0a0a;\r\n	border: 1px solid #111;\r\n	padding: 0.2em 0.4em 0.2em 0.4em;\r\n	margin: 0.2em;\r\n	text-decoration: none;\r\n}\r\n\r\n.pagenav a:hover {\r\n	color: #fff;\r\n	background-color: #0a0a0a;\r\n	margin: 0.2em;\r\n	text-decoration: none;\r\n}\r\n\r\n.header {\r\n	height: 200px;\r\n	width: 800px;\r\n	margin: 0px auto;\r\n	background-image: url(&quot;images/header.png&quot;);	\r\n}\r\n\r\n.sub-headmenu {\r\n	height: 130px;\r\n	width: 1024px;\r\n	margin: 0px auto;\r\n	background-image: url(&quot;images/subheadermenu.png&quot;);\r\n	border-right : 1px solid #333333;\r\n	border-left : 1px solid #333333;	\r\n}\r\n\r\n.reklama {\r\n	float: right;	\r\n	margin-top: 0px;\r\n	background-image: url(&quot;images/reklama.png&quot;);\r\n	background-repeat: no-repeat;\r\n	background-position: top;\r\n	color: white;\r\n}\r\n\r\n\r\nbody {\r\n \r\ncolor : #000;\r\n background : #000;\r\n background-image : url(images/bg.png);\r\n background-repeat : repeat;\r\n background-position : center top;\r\n background-attachment : fixed;\r\n font-family : Verdana, Arial, Helvetica, sans-serif;\r\n font-size : 11px;\r\n\r\n\r\n}\r\n\r\nbackground-attachment : fixed;[/code]\r\n\r\n\r\n\r\nSLIDER HTML\r\n[code]&lt;!DOCTYPE html PUBLIC &quot;-//W3C//DTD XHTML 1.0 Transitional//EN&quot; &quot;http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd&quot;&gt;\r\n&lt;html xmlns=&quot;http://www.w3.org/1999/xhtml&quot;&gt;\r\n&lt;head&gt;\r\n&lt;title&gt;JQuery Photo Slider with Semi Transparent Caption&lt;/title&gt;\r\n\r\n&lt;script type=&quot;text/javascript&quot; src=&quot;js/jquery-1.3.1.min.js&quot;&gt;&lt;/script&gt;\r\n&lt;script type=&quot;text/javascript&quot;&gt;\r\n\r\n$(document).ready(function() {		\r\n	\r\n	//Execute the slideShow\r\n	slideShow();\r\n\r\n});\r\n\r\nfunction slideShow() {\r\n\r\n	//Set the opacity of all images to 0\r\n	$(&#039;#gallery a&#039;).css({opacity: 0.0});\r\n	\r\n	//Get the first image and display it (set it to full opacity)\r\n	$(&#039;#gallery a:first&#039;).css({opacity: 1.0});\r\n	\r\n	//Set the caption background to semi-transparent\r\n	$(&#039;#gallery .caption&#039;).css({opacity: 0.7});\r\n\r\n	//Resize the width of the caption according to the image width\r\n	$(&#039;#gallery .caption&#039;).css({width: $(&#039;#gallery a&#039;).find(&#039;img&#039;).css(&#039;width&#039;)});\r\n	\r\n	//Get the caption of the first image from REL attribute and display it\r\n	$(&#039;#gallery .content&#039;).html($(&#039;#gallery a:first&#039;).find(&#039;img&#039;).attr(&#039;rel&#039;))\r\n	.animate({opacity: 0.7}, 400);\r\n	\r\n	//Call the gallery function to run the slideshow, 6000 = change to next image after 6 seconds\r\n	setInterval(&#039;gallery()&#039;,6000);\r\n	\r\n}\r\n\r\nfunction gallery() {\r\n	\r\n	//if no IMGs have the show class, grab the first image\r\n	var current = ($(&#039;#gallery a.show&#039;)?  $(&#039;#gallery a.show&#039;) : $(&#039;#gallery a:first&#039;));\r\n\r\n	//Get next image, if it reached the end of the slideshow, rotate it back to the first image\r\n	var next = ((current.next().length) ? ((current.next().hasClass(&#039;caption&#039;))? $(&#039;#gallery a:first&#039;) :current.next()) : $(&#039;#gallery a:first&#039;));	\r\n	\r\n	//Get next image caption\r\n	var caption = next.find(&#039;img&#039;).attr(&#039;rel&#039;);	\r\n	\r\n	//Set the fade in effect for the next image, show class has higher z-index\r\n	next.css({opacity: 0.0})\r\n	.addClass(&#039;show&#039;)\r\n	.animate({opacity: 1.0}, 1000);\r\n\r\n	//Hide the current image\r\n	current.animate({opacity: 0.0}, 1000)\r\n	.removeClass(&#039;show&#039;);\r\n	\r\n	//Set the opacity to 0 and height to 1px\r\n	$(&#039;#gallery .caption&#039;).animate({opacity: 0.0}, { queue:false, duration:0 }).animate({height: &#039;1px&#039;}, { queue:true, duration:300 });	\r\n	\r\n	//Animate the caption, opacity to 0.7 and heigth to 100px, a slide up effect\r\n	$(&#039;#gallery .caption&#039;).animate({opacity: 0.7},100 ).animate({height: &#039;70px&#039;},500 );\r\n	\r\n	//Display the content\r\n	$(&#039;#gallery .content&#039;).html(caption);\r\n	\r\n	\r\n}\r\n\r\n&lt;/script&gt;\r\n&lt;style type=&quot;text/css&quot;&gt;\r\nbody{\r\n	font-family:arial\r\n}\r\n\r\n.clear {\r\n	clear:both\r\n}\r\n\r\n#gallery {\r\n	position:relative;\r\n	height:200px\r\n}\r\n	#gallery a {\r\n		float:left;\r\n		position:absolute;\r\n	}\r\n	\r\n	#gallery a img {\r\n		border:none;\r\n	}\r\n	\r\n	#gallery a.show {\r\n		z-index:500\r\n	}\r\n\r\n	#gallery .caption {\r\n		z-index:600; \r\n		background-color:#444; \r\n		color:#ffffff; \r\n		height:70px; \r\n		width:100%; \r\n		position:absolute;\r\n		bottom:0;\r\n	}\r\n\r\n	#gallery .caption .content {\r\n		padding-left: 20px;\r\n		padding-top: 10px;\r\n		margin:5px;\r\n		font-size: 11px;\r\n	}\r\n	\r\n	#gallery .caption .content h3 {\r\n		margin:0;\r\n		padding:0;\r\n		font-size:15px;\r\n		color:white;\r\n		font-weight: bold;\r\n	}\r\n	\r\n\r\n&lt;/style&gt;\r\n&lt;/head&gt;\r\n&lt;body&gt;\r\n\r\n&lt;div id=&quot;gallery&quot;&gt;\r\n\r\n	&lt;a href=&quot;#&quot; class=&quot;show&quot;&gt;\r\n		&lt;img src=&quot;http://cdn.imghack.se/images/4869ffcacf5bd63c839f594bfc6fbff1.jpg&quot; alt=&quot;Lorem ipsum&quot; width=&quot;698&quot; height=&quot;200&quot; title=&quot;&quot; alt=&quot;&quot; rel=&quot;&lt;h3&gt; [DND_Joseph] !&lt;/h3&gt;Vitejte na me strance 1x15 sawn off shotgun&quot;/&gt;\r\n	&lt;/a&gt;\r\n	\r\n	&lt;div class=&quot;caption&quot;&gt;&lt;div class=&quot;content&quot;&gt;&lt;/div&gt;&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;div class=&quot;clear&quot;&gt;&lt;/div&gt;\r\n\r\n&lt;/body&gt;\r\n&lt;/html&gt;[/code]', '1422995695', '93.91.154.28', '915', '1422997484', '416'),
(2952, '912', '@Trsak Dík za radu, už to mám a funguje to bombovo\r\n\r\nTeraz som to parádne vyriešil a páči sa mi to aj ako to funguje už definoval som si aj názvy súborov stačí mi prepísať názov zložky a aj v global.php čo je môj súbor kde mám tieto definície a mám ;) \r\n\r\nTriedy mám pekne spravené v zložke jednej, kde su všetky triedy v osobitnom subore .php koncóvku .class.php som nepoužil nevidím na to dôvod je to jedno.\r\n\r\nDalej som si spravil nieto takéto na čítanie triéd z jednej zložky , ktorá je daná.\r\n\r\n[code]//Trieda Framework\r\nClass Framework{\r\n\r\n	//Načítanie triedy zo suboru app/class/ :)\r\n	public function nacitaj_triedu($trieda){\r\n\r\n		//Zistíme kde je tento súbor\r\n		$subor = appclass.$trieda.&quot;.php&quot;;\r\n\r\n		//Zistenie súboru či existuje\r\n		if(file_exists($subor)){\r\n			//Súbor sa našiel\r\n			require_once($subor);\r\n		}else{\r\n			//Súbor sa nenašiel\r\n			echo &quot;Nenašla sa trieda &quot;.$trieda.&quot;&quot;;\r\n		}\r\n	}\r\n\r\n}[/code]\r\n\r\nTak isto na kódy typu require_once som použil toto\r\n\r\n\r\n[code]\r\n//Root webu \r\ndefine(&quot;BASE_PATH&quot;, dirname(realpath(__FILE__)).&quot;/&quot;); \r\n[/code]\r\n\r\nnašiel som to v jednom článku :) myslím si že všetky argumenty čo sem boli kritizované sú upravené a zmenené ;)', '1423055724', '87.244.228.4', '912', '1423079164', '415'),
